import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import TodoItem from './components/TodoItem';

class App extends Component {
  state = {
    todoItems : [
      { title: 'Di lam', checkComplete:true},
      { title:'An com trua', checkComplete:true},
      { title:'An com toi', checkComplete:false},
      { title:'Choi game', checkComplete:false},
    ],
    kethua: [{name: 'riem'},{name: 'hau'},{name: 'nhat'}]
  }

  handleClick = (e) => {
    console.log(e)
    const index = this.state.todoItems.indexOf(e);
    const {todoItems} = this.state;
    console.log(index)
    this.setState({
      todoItems:[]
    })
  }
  render() {
    const {todoItems} = this.state;
    return (
      <div className="App">
        {
          todoItems.map((item,index) => 
          <TodoItem key={index} item={item} onClick={this.handleClick}/>)
        }
      </div>
    );
  }
}

export default App;
